<?php 
function image($ID, $size, $class = '', $alt = 'Feature Image', $loading = 'lazy') {
    // Handle auto width with fixed height
    if (is_array($size) && $size[0] === 'auto') {
        $height = $size[1];
        $image_url = wp_get_attachment_image_url($ID, 'large');
        echo '<img src="' . esc_url($image_url) . '" class="' . esc_attr($class) . '" alt="' . esc_attr($alt) . '" loading="' . esc_attr($loading) . '" aria-label="' . esc_attr($alt) . '" title="' . esc_attr($alt) . '" draggable="false" style="height: ' . $height . 'px; width: auto; object-fit: cover;">';
    } else {
        echo wp_get_attachment_image($ID, $size, false, 
            array(
                'class' => $class, 
                'alt' => $alt,
                'loading' => $loading,
                'aria-label' => $alt,
                'title' => $alt,
                'draggable' => 'false'
            )
        );
    }
}

function video($src, $mime_type, $class = '', $settings = '', $poster = '', $captions = '') { ?>
    <video class="<?php echo $class; ?>" <?php echo $settings?> poster="<?php echo $poster; ?>">
        <source src="<?php echo $src; ?>" type="<?php echo $mime_type; ?>">
        <?php if ($captions): ?>
            <track kind="captions" src="<?php echo $captions; ?>" srclang="en">
        <?php endif; ?>
        Your browser does not support the video tag.
    </video>
<?php }

// Optimized video function with mobile/desktop support and fallbacks
function responsive_video($desktop_video, $mobile_video = null, $class = '', $settings = '', $poster = '', $captions = '') {
    // If no mobile video provided, use desktop for all devices
    if (!$mobile_video) {
        video($desktop_video['url'], $desktop_video['mime_type'], $class, $settings, $poster, $captions);
        return;
    }
    
    // Server-side device detection for optimal performance
    $is_mobile = wp_is_mobile();
    $selected_video = $is_mobile ? $mobile_video : $desktop_video;
    
    // Add data attributes for JavaScript enhancement (optional)
    $enhanced_settings = $settings;
    if (strpos($settings, 'data-responsive') === false) {
        $enhanced_settings .= ' data-responsive="true"';
    }
    ?>
    <video class="<?php echo $class; ?>" <?php echo $enhanced_settings; ?> poster="<?php echo $poster; ?>">
        <?php if ($is_mobile && $mobile_video): ?>
            <source src="<?php echo $mobile_video['url']; ?>" type="<?php echo $mobile_video['mime_type']; ?>" media="(max-width: 768px)">
        <?php endif; ?>
        <source src="<?php echo $desktop_video['url']; ?>" type="<?php echo $desktop_video['mime_type']; ?>" media="(min-width: 769px)">
        <!-- Fallback source for all devices -->
        <source src="<?php echo $selected_video['url']; ?>" type="<?php echo $selected_video['mime_type']; ?>">
        <?php if ($captions): ?>
            <track kind="captions" src="<?php echo $captions; ?>" srclang="en">
        <?php endif; ?>
        Your browser does not support the video tag.
    </video>
<?php }

function button($text, $link = '#', $target = '_self') {
    $is_external_link = $target == '_blank' ? true : false;
    ob_start();
    if ($is_external_link) {
        icon_external_link();
    }
    $icon_svg = ob_get_clean();
    
    echo '<a href="' . $link . '" class="button" target="' . $target . '">' . $text . $icon_svg . '</a>';
}

