<?php defined( 'ABSPATH' ) || exit;$newsletter = get_field('newsletter', 'option');$pre_heading = $newsletter['pre_heading'];$heading = $newsletter['heading'];$description = $newsletter['description'];?><div class="newsletter --form">    <div class="newsletter__content">        <?php if ($pre_heading): ?>            <p class="newsletter__pre-heading text__size-5"><?php echo $pre_heading; ?></p>        <?php endif; ?>        <?php if ($heading): ?>            <p class="newsletter__heading text__size-1"><?php echo $heading; ?></p>        <?php endif; ?>        <?php if ($description): ?>            <p class="newsletter__description text__size-4"><?php echo $description; ?></p>        <?php endif; ?>        <div class="newsletter__form">            <?php echo do_shortcode('[convertkit_form form="959622"]'); ?>        </div>    </div></div>